import React from 'react';
import { ClassNames } from 'helpers/ClassNames/ClassNames';
import cls from './ClassItem.module.css';
import { Button, ButtonTheme } from 'UI/Button/Button';
import { ReactComponent as Arrow} from 'assets/icons/arrow-top.svg'

export const ClassItem = (props) => {
    const { 
        className,
        item
    } = props;

    return (
        <div className={ClassNames(cls.classItem, {}, [className])}>
            <span className={cls.title} >{item.name}  <Arrow className={cls.icon} /> </span>
            <div className={ClassNames(cls.body, {}, [])}>
                {
                    item.students.map(student => (
                        <div className={cls.row} ></div> 
                    ))
                }
                <Button theme={ButtonTheme.DARK}>Добавить ученика</Button>
            </div>
        </div>
 );
}
