import { createSlice } from "@reduxjs/toolkit"


export const PopupNames = {
    CHOSE: 'chose',
    SIGNIN: 'signin',
    SIGNUP: 'signup'
}


const initialState = {
    popups: [
        {
            name: PopupNames.CHOSE,
            hidden: true
        },
        {
            name: PopupNames.SIGNIN,
            hidden: true
        },
        {
            name: PopupNames.SIGNUP,
            hidden: true
        },
    ]
}


export const PopupsSlice = createSlice({
    initialState: initialState,
    name: 'popups',
    reducers: {
        showPopup(state, action){
            state.popups.forEach(popup =>  popup.hidden = true )
            state.popups.find(popup =>  popup.name === action.payload ).hidden = false 
        },
        closePopup(state, action){
            state.popups.find(popup =>  popup.name === action.payload ).hidden = true
        },
        closeAllPopups(state){
            state.popups.forEach(popup =>  popup.hidden = true )
        },
        switchAuthentication(state){
            const popupSignin = state.popups.find(popup => popup.name === PopupNames.SIGNIN)
            const popupSignup = state.popups.find(popup => popup.name === PopupNames.SIGNUP)
            console.log('signin ', popupSignin.hidden)
            console.log('signup ', popupSignup.hidden)

            if(popupSignin.hidden){
                state.popups.forEach(popup =>  popup.hidden = true )
                popupSignin.hidden = false
            }else {
            console.log('1234')
                state.popups.forEach(popup =>  popup.hidden = true )
                popupSignup.hidden = false
            }
        }

    }
})
