import { createSlice } from '@reduxjs/toolkit'

const initiatlState = {
    tasks: []

}


export const TestCreationSlice = createSlice({
    initialState: initiatlState,
        name: 'testCreation',
        reducers: {
            addRandomQuestion(state){
                state.tasks.push({
                    id: 1,
                    type: 'checkbox',
                    question: 'Что НЕ относится к персональным данным ?',
                    answers: [
                        {
                            text: 'Фамилия, имя, отчество',
                            isCorrect: false
                        },
                        {
                            text: 'Все наши документы',
                            isCorrect: false
                        },
                        {
                            text: 'Банковские данные',
                            isCorrect: false
                        },
                        {
                            text: 'Картинка природы из Интернета',
                            isCorrect: true
                        },
                    ],
                    isPersonal: false
                },)
            }
        }
})
