export function ClassNames(cls, mods, anotherClasses){
//Функция для удобного использования классов

    return [
        cls,
        ...anotherClasses,
        ...Object.entries(mods)
            .filter(([className, value]) => Boolean(value))
            .map(([className]) => className)
    ].join(' ')
}


ClassNames(cls.btn, {'dark': dark, 'light': light}, [])
